IDENTITY=${1:-default}

SCRIPT=$(readlink -f "$0")
SCRIPT_DIR=$(dirname "$SCRIPT")
cd $SCRIPT_DIR/..

PRINCIPAL=$(dfx --identity $IDENTITY identity get-principal)

./scripts/download-nns-canister-wasm.sh icrc_ledger ic-icrc1-ledger
dfx --identity $IDENTITY canister create --with-cycles 100000000000000 test_chat_ledger
dfx --identity $IDENTITY canister install test_chat_ledger --wasm ./wasms/icrc_ledger.wasm.gz --argument "(variant { Init = record {
    minting_account = record { owner = principal \"2jvtu-yqaaa-aaaaq-aaama-cai\" };
    transfer_fee = 100000:nat;
    decimals = 8:nat8;
    token_symbol = \"CHAT\";
    token_name = \"CHAT\";
    metadata = vec {
        record {
            \"icrc1:logo\";
            variant {
                Text = \"data:image/png;base64,\"
            };
        };
    };
    initial_balances = vec {
        record {
            record { owner = principal \"$PRINCIPAL\" };
            1_000_000_000_000_000:nat;
        };
    };
    archive_options = record {
        num_blocks_to_archive = 1000:nat64;
        trigger_threshold = 1000:nat64;
        controller_id = principal \"$PRINCIPAL\";
    };
}})"

dfx --identity $IDENTITY canister call registry add_token "(record {
    ledger_canister_id = principal \"2ouva-viaaa-aaaaq-aaamq-cai\";
    payer = null;
    info_url = \"https://dashboard.internetcomputer.org/sns/3e3x2-xyaaa-aaaaq-aaalq-cai\";
    transaction_url_format = \"https://dashboard.internetcomputer.org/sns/3e3x2-xyaaa-aaaaq-aaalq-cai/transactions/{transaction_index}\";
})"
